  {user_id: 5, name: "Minichat", description: "", source: "<html> \r\n    <head>\r\n        \r\n        <link rel=\"stylesheet\" href=\"/vendor/bootstrap-3.3.7-dist/css/bootstrap.min.css\">\r\n        <link rel=\"stylesheet\" href=\"/vendor/bootstrap-3.3.7-dist/css/bootstrap-theme.min.css\">\r\n        <link rel=\"stylesheet\" href=\"/vendor/font-awesome-4.7.0/css/font-awesome.min.css\">\r\n    \r\n        <script type=\"text/javascript\" src=\"/vendor/jquery-3.2.0.min.js\"></script>\r\n        <script type=\"text/javascript\" src=\"/vendor/jquery-form.js\"></script>\r\n        <script type=\"text/javascript\" src=\"/vendor/bootstrap-3.3.7-dist/js/bootstrap.min.js\"></script>\r\n        <script type=\"text/javascript\" src=\"/vendor/time_ago.js\"></script>\r\n\r\n    </head>\r\n\r\n      <div class=\"container\">\r\n        <div class=\"row\">\r\n          <div class=\"col-sm-4 col-md-3 col-lg-3\">\r\n            <h4>\r\n              <span id=\"server_connection_status\" style=\"color:#F90\">\r\n                <i class=\"fa fa-wifi\"></i>\r\n              </span>\r\n              <span id='minichat_title'>Minichat</span>\r\n            </h4>\r\n            <div id=\"rooms\" style=\"display:none;\">\r\n              <form id=\"new_room_form\" action=\"/servers/chat_studio/api/v0/rooms.json\" name=\"new_room_form\" method=\"post\">\r\n                <input name=\"room[name]\" id=\"input_room_name\" placeholder=\"New room\" type=\"text\" class=\"form-control\" autocomplete=\"off\">\r\n              </form>\r\n              <p><div id=\"rooms_list\"></div></p>\r\n            </div>\r\n          </div>\r\n          <div id=\"room\" class=\"col-sm-8 col-md-9 col-lg-9\" style=\"display:none;\">\r\n            <h4 id=\"room_title\"></h4>\r\n            <form id=\"new_message_form\" action=\"/servers/chat_studio/api/v0/messages.json\" name=\"new_message_form\" method=\"post\">\r\n              <input name=\"message[room_id]\" id=input_message_room_id type=\"hidden\">\r\n              <input name=\"message[body]\" id=\"input_message_body\" placeholder=\"New message\" type=\"text\" class=\"form-control\" autocomplete=\"off\">\r\n            </form>\r\n            <p><div id=\"room_messages\"></div></p>\r\n          </div>\r\n          <div id=\"loading\" class=\"col-sm-12 text-center\">\r\n            <i class=\"fa fa-spinner fa-spin\"></i>\r\n          </div>\r\n        </div>\r\n      </div>\r\n\r\n    <div id=\"stream\"></div>\r\n\r\n    <body>\r\n        <script>\r\n            $( document ).ready(function() {\r\n            \r\n                bindAjaxForm();\r\n\r\n                $.ajaxSetup({ headers : { 'Authorization': 'Token ' + '__API_TOKEN__' } });\r\n    \r\n                $.get(\"/servers/chat_studio/api/v0/rooms.json\", function(rooms) {\r\n                    $.each(rooms, function(index, room){\r\n                      $('#rooms_list').prepend(roomHtml(room));\r\n                    });\r\n                    $(\"time.timeago\").timeago();\r\n                    $(\"#loading\").hide();\r\n                    $(\"#rooms\").fadeIn();\r\n                });\r\n                \r\n                $(\"#rooms_list\").on(\"click\", \"a.room_link\", function(event){\r\n                    var roomId = $(this).data(\"roomid\");\r\n                    $.get((\"/servers/chat_studio/api/v0/rooms/\" + roomId + \".json\"), function(room) {\r\n                      $(\"#room\").hide();\r\n                      $(\"#room_messages\").empty();\r\n                      $(\"#room_title\").html(room.name + ' room');\r\n                      $(\"#input_message_room_id\").val(room.id);\r\n                      $.each(room.messages, function(index, message) {\r\n                        $(\"#room_messages\").prepend(messageHtml(message));\r\n                      });\r\n                      $(\"time.timeago\").timeago();\r\n                      bindAjaxForm();\r\n                      $(\"#room\").fadeIn();\r\n                    });\r\n 