  {user_id: 5, name: "Fancy name", description: nil, source: "<!DOCTYPE html>\r\n<html>\r\n\r\n  <head>\r\n    <title>Fancy name</title>\r\n    <script type=\"text/javascript\" src=\"/vendor/jquery-3.2.0.min.js\"></script>\r\n\r\n        <style>\r\n\r\n            *, *:before, *:after {\r\n                margin: 0;\r\n                padding: 0;\r\n                box-sizing: border-box;\r\n                position: relative;\r\n            }\r\n            \r\n            html, body {\r\n                height: 100%;\r\n            }\r\n              \r\n            body {\r\n                text-align: center;\r\n                background-color: hsla(230,40%,50%,1);\r\n            }\r\n            \r\n            body:before {\r\n                content: '';\r\n                display: inline-block;\r\n                vertical-align: middle;\r\n                font-size: 0;\r\n                height: 100%;\r\n            }\r\n            \r\n            h4 {\r\n                display: inline-block;\r\n                color: white;\r\n                font-family: serif;\r\n                font-size: 6em; \r\n                text-shadow: .03em .03em 0 hsla(230,40%,50%,1);\r\n            }\r\n\r\n            h4:after {\r\n                content: attr(data-shadow);\r\n                position: absolute;\r\n                top: .06em; left: .06em;\r\n                z-index: -1;\r\n                text-shadow: none;\r\n                background-image:\r\n                  linear-gradient(\r\n                    45deg,\r\n                    transparent 45%,\r\n                    hsla(48,20%,90%,1) 45%,\r\n                    hsla(48,20%,90%,1) 55%,\r\n                    transparent 0\r\n                    );\r\n                background-size: .05em .05em;\r\n                -webkit-background-clip: text;\r\n                -webkit-text-fill-color: transparent;\r\n              \r\n                animation: shad-anim 15s linear infinite;\r\n            }\r\n  