  {user_id: 5, name: "Plainchat 0.0.1", description: nil, source: "<html>\r\n<head>\r\n    <title>Plainchat</title>        \r\n</head>\r\n<body>\r\n\r\n    <h3>Plainchat</h3>\r\n    <button onclick=\"loadRoomsList()\">Rooms</button>\r\n    <pre id=\"rooms_list\"></pre>\r\n\r\n    <script>\r\n        function loadRoomsList() {\r\n            var http = new XMLHttpRequest();\r\n            http.open(\"GET\", \"/servers/chat_studio/api/v0/rooms.json\", true);\r\n            http.setRequestHeader ('Authorization', 'Token ' + '__API_TOKEN__');\r\n            http.onreadystatechange = function() {\r\n                if(http.readyState == 4) {\r\n                    if (http.status == 200) {\r\n                        var element = document.getElementById('rooms_list');\r\n                        element.innerHTML = http.responseText;\r\n                        // element.innerHTML = '<pre>' + JSON.stringify(JSON.parse(http.responseText),null,2) + '</pre>';\r\n                    } else {\r\n                        alert(\"Error. Could not load rooms. \" + http.responseText);\r\n                    };\r\n                };\r\n            }\r\n            http.send();\r\n        };\r\n    </script>\r\n\r\n</body>\r\n</html>", published: true, sort_order: 14},
  {user_id: 5, name: "Plainchat 0.0.2", description: nil, source: "<html>\r\n<head>\r\n    <title>Plainchat 0.0.2</title>        \r\n    <script type=\"text/javascript\" src=\"/vendor/jquery-3.2.0.min.js\"></script>\r\n</head>\r\n<body>\r\n\r\n    <h3>Plainchat</h3>\r\n    <p>Now with Jquery!</p>\r\n    <button id=\"load_rooms\">Load rooms</button>\r\n    <pre id=\"rooms_list\"></pre>\r\n\r\n    <script>\r\n\r\n        $.ajaxSetup({ headers : { 'Authorization': 'Token ' + '__API_TOKEN__' } });\r\n\r\n        $(\"#load_rooms\").click( loadRooms )\r\n\r\n        function loadRooms() {\r\n            $('#rooms_list').hide();\r\n            $.get(\"/servers/chat_studio/api/v0/rooms\", function(rooms) {\r\n                $(\"#rooms_list\").html( JSON.stringify(rooms,null,2) );\r\n            });            \r\n            $('#rooms_list').fadeIn();\r\n        };\r\n\r\n    </script>\r\n\r\n</body>\r\n</html>", published: true, sort_order: 15},
